import React, { useState, useEffect } from 'react';
import Web3 from 'web3';

function App() {
  const [account, setAccount] = useState('');
  const [products, setProducts] = useState([]);
  const web3 = new Web3(window.ethereum);

  const [abi, setAbi] = useState(null);
  const [contractAddress, setContractAddress] = useState(null);

 useEffect(() => {
    // Funzione per connettere il wallet MetaMask
    const connectWallet = async () => {
      if (window.ethereum) {
        const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
        setAccount(accounts[0]);
      }
    };

    connectWallet();
  }, []);


  useEffect(() => {
    const fetchContractData = async () => {
      try {
        const response = await fetch('/SupplyChainTracker.json'); // Adatta il percorso al tuo file
        const data = await response.json();
        setAbi(data.abi);
        const abi = data.abi;
        setContractAddress(data.networks.development.address);
        const contractAddress = data.networks.development.address;
        console.log(contractAddress);
      } catch (error) {
        console.error('Error fetching contract data:', error.toString());
      }
    };

    fetchContractData();
  }, []);

  // Funzione per ottenere i prodotti dell'utente
  const getProducts = async () => {
  const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
  const account = accounts[0];

  const products = await contract.methods.getProducts(account).call();
  setProducts(products);
};


  // Funzione per aggiungere un prodotto
  const addProduct = async (name, manufacturer, serialNumber, initialOwner, initialLocation) => {
    // ... codice per aggiungere un prodotto
  };

  // ... altre funzioni per interagire con il contratto

  return (
    <div>
      <h1>Supply Chain Tracker</h1>
      <p>Account: {account}</p>
      <button onClick={getProducts}>Get Products</button>
      {/* Visualizzazione dei prodotti */}
    </div>
  );
}

export default App;




