import React, { useState, useEffect } from 'react';
import Web3 from 'web3';
import SupplyChainTracker from './contracts/SupplyChainTracker.json';

const App = () => {
  const [web3, setWeb3] = useState(null);
  const [contract, setContract] = useState(null);
  const [account, setAccount] = useState('');
  const [productCount, setProductCount] = useState(0);

  useEffect(() => {
    const init = async () => {
      // Connessione a Web3
      if (window.ethereum) {
        const web3Instance = new Web3(window.ethereum);
        try {
          // Richiedi l'accesso all'account
          await window.ethereum.enable();
          setWeb3(web3Instance);

          // Ottieni l'account corrente
          const accounts = await web3Instance.eth.getAccounts();
          setAccount(accounts[0]);

          // Ottieni l'istanza del contratto
          const networkId = await web3Instance.eth.net.getId();
          const deployedNetwork = SupplyChainTracker.networks[networkId];
          const instance = new web3Instance.eth.Contract(
            SupplyChainTracker.abi,
            deployedNetwork && deployedNetwork.address,
          );
          setContract(instance);

          // Ottieni il conteggio dei prodotti
          const count = await instance.methods.productCount().call();
          setProductCount(count);
        } catch (error) {
          console.error("Errore durante l'inizializzazione di Web3", error);
        }
      }
    };
    init();
  }, []);

  const incrementProductCount = async () => {
    if (contract && account) {
      try {
        await contract.methods.incrementProductCount().send({ from: account });
        const newCount = await contract.methods.productCount().call();
        setProductCount(newCount);
      } catch (error) {
        console.error("Errore durante l'incremento del conteggio", error);
      }
    }
  };

  return (
    <div>
      <h1>Supply Chain Tracker</h1>
      <p>Account connesso: {account}</p>
      <p>Conteggio prodotti: {productCount}</p>
      <button onClick={incrementProductCount}>Incrementa conteggio</button>
    </div>
  );
};

export default App;
