import React, { useState, useEffect } from 'react';
import Web3 from 'web3';

function App() {
  const [account, setAccount] = useState('');
  const [products, setProducts] = useState([]);
  const [contract, setContract] = useState(null);

  useEffect(() => {
    const connectWallet = async () => {
      if (window.ethereum) {
        const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
        setAccount(accounts[0]);
      }
    };

    connectWallet();
  }, []);

  useEffect(() => {
    const fetchContractData = async () => {
      try {
        const response = await fetch('/SupplyChainTracker.json');
        const data = await response.json();
        const abi = data.abi;
        const contractAddress = data.networks.development.address; // Replace with correct network

        const web3 = new Web3(window.ethereum);
        const contractInstance = new web3.eth.Contract(abi, contractAddress);
        setContract(contractInstance);
      } catch (error) {
        console.error('Error fetching contract data:', error);
      }
    };

    fetchContractData();
  }, []);

  const getProducts = async () => {
    try {
      const productCount = await contract.methods.productCount().call();
      const products = [];

      for (let i = 0; i < productCount; i++) {
        const product = await contract.methods.products(i).call();
        products.push(product);
      }

      setProducts(products);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  const addProduct = async (productName, productDescription) => {
    try {
      console.log(productName + " " + productDescription + " from " + account);
      await contract.methods.addProduct(productName, productDescription).send({ from: account });
      console.log('Product added successfully');
    } catch (error) {
      console.error('Error adding product:', error);
    }
  };

  const handleAddProduct = (e) => {
    e.preventDefault();
    const productName = e.target.productName.value;
    const productDescription = e.target.productDescription.value;
    addProduct(productName, productDescription);
  };

  return (
    <div>
      <h1>Supply Chain Tracker</h1>
      <p>Account: {account}</p>
      <button onClick={getProducts}>Get Products</button>
      <form onSubmit={handleAddProduct}>
      <label htmlFor="productManufacturer">Manufacturer:</label>
        <input type="text" id="productManufacturer" name="productManufacturer" required />
        <br />
        <label htmlFor="productName">Product Name:</label>
        <input type="text" id="productName" name="productName" required />
        <br />
        <label htmlFor="productSerialNumber">Serial Number:</label>
        <input type="text" id="productSerialNumber" name="productSerialNumber" required />
        <br />
        <button type="submit">Add Product</button>
      </form>
      <ul>
        {products.map((product, index) => (
          <li key={index}>{product.name} - {product.description}</li>
        ))}
      </ul>
    </div>
  );
}

export default App;
